mapper.select:
select 

from public.w_upload_sub
where upload_user_id = #{uploadUserId,jdbcType=VARCHAR}
  and upload_user_ts = #{uploadUserTs,jdbcType=TIMESTAMP}
  and upload_session = #{uploadSession,jdbcType=VARCHAR}

mapper.delete:
delete from public.w_upload_sub
where upload_user_id = #{uploadUserId,jdbcType=VARCHAR}
  and upload_user_ts = #{uploadUserTs,jdbcType=TIMESTAMP}
  and upload_session = #{uploadSession,jdbcType=VARCHAR}

mapper.update:
update public.w_upload_sub


    process_flag = #{processFlag,jdbcType=CHAR},


    register_id = #{registerId,jdbcType=VARCHAR},


    regist_ts = #{registTs,jdbcType=TIMESTAMP},


    updater_id = #{updaterId,jdbcType=VARCHAR},


    update_ts = #{updateTs,jdbcType=TIMESTAMP},


where upload_user_id = #{uploadUserId,jdbcType=VARCHAR}
  and upload_user_ts = #{uploadUserTs,jdbcType=TIMESTAMP}
  and upload_session = #{uploadSession,jdbcType=VARCHAR}

update public.w_upload_sub
set process_flag = #{processFlag,jdbcType=CHAR},
  register_id = #{registerId,jdbcType=VARCHAR},
  regist_ts = #{registTs,jdbcType=TIMESTAMP},
  updater_id = #{updaterId,jdbcType=VARCHAR},
  update_ts = #{updateTs,jdbcType=TIMESTAMP}
where upload_user_id = #{uploadUserId,jdbcType=VARCHAR}
  and upload_user_ts = #{uploadUserTs,jdbcType=TIMESTAMP}
  and upload_session = #{uploadSession,jdbcType=VARCHAR}
